import { useEffect, useState } from "react"
import {getGifs} from '../helpers/getGifs'

export const useFetchGifs = (category) => {
    const [state, setState] = useState({
        data: [],
        loading: true
    });

    useEffect ( (category) => {
        getGifs(cateory)
    }, [])

    return state; //{data:[], loading: true};
}
